svg.script:

function showInfo(id) {
  var box = document.getElementById(id + '-info-box');
  var text = document.getElementById(id + '-info-text');
  var text2 = document.getElementById(id + '-info-text2');
  if (box) box.style.display = 'block';
  if (text) text.style.display = 'block';
  if (text2) text2.style.display = 'block';
}

function hideInfo(id) {
  var box = document.getElementById(id + '-info-box');
  var text = document.getElementById(id + '-info-text');
  var text2 = document.getElementById(id + '-info-text2');
  if (box) box.style.display = 'none';
  if (text) text.style.display = 'none';
  if (text2) text2.style.display = 'none';
}
